mapper.select:
select count(*)
from member
where id = #{INPUTID}


SELECT ID, NAME, BLOCK
FROM MEMBER 
WHERE NAME = #{NAME} AND EMAIL = #{EMAIL}


SELECT ID, NAME, EMAIL, BLOCK
FROM MEMBER 
WHERE ID = #{ID} AND NAME = #{NAME} AND EMAIL = #{EMAIL}

mapper.update:

 UPDATE MEMBER
    SET BLOCK = 'Y'
WHERE NUM = #{str_Num}


UPDATE MEMBER
   SET PASSWORD = #{PASSWORD}
   WHERE ID = #{ID}

mapper.select:
SELECT G.GOODS_NUM, G.GOODS_NAME, ORDER_GOODS_IMAGE, ORDER_NUM, ORDER_STATUS, ORDER_SUM_MONEY, ORDER_MEMBER_ID, ORDER_DATE, ORDER_GOODS_AMOUNT
FROM GOODS_ORDER O, GOODS G, MEMBER M
WHERE G.GOODS_NUM = O.ORDER_GOODS_NUM AND M.ID = O.ORDER_MEMBER_ID AND M.ID = #{ID}

SELECT ORDER_GOODS_NUM, ORDER_GOODS_NAME, ORDER_GOODS_IMAGE, ORDER_NUM, ORDER_STATUS, ORDER_SUM_MONEY, ORDER_MEMBER_ID, ORDER_DATE, ORDER_GOODS_AMOUNT, ORDER_RECEIVE_NAME, ORDER_RECEIVE_ADDR1, ORDER_RECEIVE_ADDR2, ORDER_RECEIVE_PHONE
FROM GOODS_ORDER O, GOODS G, MEMBER M 
WHERE G.GOODS_NUM = O.ORDER_GOODS_NUM AND M.ID = O.ORDER_MEMBER_ID AND M.ID = #{ID} AND ORDER_NUM = #{ORDER_NUM}

mapper.update:
UPDATE GOODS_ORDER
SET ORDER_STATUS = '주문취소'
WHERE ORDER_NUM = #{ORDER_NUM}

UPDATE GOODS
SET GOODS_AMOUNT + ${GOODS_AMOUNT}
WHERE GOODS_NUM = #{GOODS_NUM}
