mapper.select:

SELECT QNA_NUM, QNA_GOODS_NUM, QNA_ID, QNA_SUBJECT, QNA_CONTENTS, QNA_IMAGE, QNA_DATE, QNA_DELETE, QNA_STATUS, QNA_COMMENT 
FROM QNA
WHERE QNA_NUM = #{QNA_NUM}

mapper.update:

UPDATE QNA
SET QNA_CONTENTS = #{QNA_CONTENTS}
WHERE QNA_NUM = #{QNA_NUM}


UPDATE QNA
SET QNA_DELETE = 'Y'
WHERE QNA_NUM = #{QNA_NUM}


UPDATE QNA
SET QNA_DELETE = 'Y'
WHERE QNA_NUM = #{QNA_NUM}


UPDATE QNA 
SET QNA_COMMENT = #{QNA_COMMENT}
WHERE QNA_NUM = #{QNA_NUM}
