mapper.update:

UPDATE ORDER_DELI
SET ORDER_STATUS = #{ORDER_STATUS}
WHERE ORDER_NUM = #{ORDER_NUM}


UPDATE ORDER_DELI
SET ORDER_STATUS = 5
WHERE ORDER_NUM = #{ORDER_NUM}

mapper.select:

SELECT ORDER_GOODS_NUM, ORDER_GOODS_AMOUNT
FROM  ORDER_GOODS
WHERE ORDER_NUM = #{ORDER_NUM}

mapper.update:

UPDATE GOODS
SET GOODS_AMOUNT = GOODS_AMOUNT + #{ORDER_GOODS_AMOUNT}
WHERE GOODS_NUM = #{ORDER_GOODS_NUM}
